import './App.js';
import './App.scss';
import React, { useState } from 'react';
import data from './clubs.json';
console.log(data);

function App() {
  /*Variables*/
  const clubsInfo = data;
  /*Variables de estado*/
  const [club, setClub] = useState('');
  /*Funciones*/
  const handleClub = (event) => {
    setClub(event.target.value);
  };

  const htmlClubList = data.map((club, index) => (
    <li className='club'>
      <h2 className='name'>{club.name}</h2>
      <label className='weekdays'>
        Abierto entre semana:{club.openOnWeekdays}
      </label>
      <label className='weekends'>
        Abierto el fin de semana:{club.openOnWeekend}
      </label>
    </li>
  ));

  return (
    <>
      <header>
        <h1 className='title'>Mis clubs:</h1>
        <form className='form'>
          <select
            name='select'
            className='select'
            onChange='/*{handleOptions}*/'
          >
            <option value=''>todos mis clubs</option>
            <option value=''>los que abren entre semana</option>
            <option value=''>los que abren el fin de semana</option>
          </select>
        </form>
      </header>

      <main className='header'>
        <ul className='list'>
          <li className='club'>
            <h2 className='name'>{club.name}</h2>
            <label className='weekdays'>
              Abierto entre semana:{club.openOnWeekdays}
            </label>
            <label className='weekends'>
              Abierto el fin de semana:{club.openOnWeekend}
            </label>
          </li>
        </ul>
        <form className='form2'>
          <h2>Añadir un nuevo club</h2>
          <label>
            Nombre del club
            <input
              type='text'
              className='textarea'
              name='description'
              value=''
            />
          </label>
          <label>
            ¿Abre entre semana?
            <input type='checkbox'></input>
          </label>
          <label>
            ¿Abre el fin de semana?
            <input type='checkbox'></input>
          </label>
        </form>
      </main>
    </>
  );
}

export default App;

