import './App.js';
import './App.scss';
import React, { useState } from 'react';

function App() {
  const handleInput = (ev) => {
    console.log('El botón ha sido pulsado');
    console.log('El evento lanzado es: ', ev);
    console.log('La tecla pulsada es: ', ev.key);
    console.log('El valor del input es: ', ev.target.value);
  };
  return (
    <>
      <div className='App'>
        {' '}
        <h1>Adivina el número</h1>
        <form action='' method='GET' class='form'>
          <label class='body__form--label' for='number'>
            Introduce aquí tu número:
          </label>
          <input class='js-number form--number' type='number' />
          <input
            class='js-button form--button'
            type='button'
            value='Prueba'
            onClick={handleInput}
          />
          <p class='js-clue form--clue'>
            Pista: Escribe el numero y dale a Prueba.
          </p>
          <input
            class='js-tries form--count'
            type='text'
            name='count'
            value='0'
          />
        </form>
        <script src='./js/main.js'></script>
      </div>
    </>
  );
}

export default App;
