import './App.js';
import './App.scss';
import React, { useState } from 'react';
//import datosArray from '..data/articles.jason'; Ya están cerrados en una variable de un array

function App() {
  /*Variables*/

  /*Varibles de estado */
  const [color, setColor] = useState('');

  /*Funciones*/
  const handleColor = (ev) => {
    setColor(ev.target.value);
  };
  const handlePlayer = (ev) => {
    console.log(ev.target.value);
  };

  return (
    <>
      <div className='App'>
        <h1 className={color}>Adivina el número</h1>
        <form action='' method='GET' className='form'>
          <label className='body__form--label' for='number'>
            Introduce aquí tu número:
          </label>
          <input className='js-number form--number' type='number' />
          <input
            className='js-button form--button'
            type='button'
            value='Prueba'
          />
          <p className='js-clue form--clue'>
            Pista: Escribe el numero y dale a Prueba.
          </p>
          <input
            className='js-tries form--count'
            type='text'
            name='count'
            value='0'
          />
        </form>
        <form>
          <input
            type='text'
            name='author'
            class='author'
            onKeyUp={handlePlayer}
          />
          <select name='select' className='select' onChange={handleColor}>
            <option value='select'>Select</option>
            <option value='pink'>Pink</option>
            <option value='blue'>Blue</option>
            <option value='purple'>Purple</option>
          </select>
        </form>
        <small></small>
      </div>
    </>
  );
}

export default App;
